import React, {Component} from 'react';
import axios from 'axios';

export default class EditDesc extends Component {

    constructor(props) {
        super(props);

        this.onChangeURI = this.onChangeURI.bind(this);
        this.onChangeTD = this.onChangeTD.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            uri: '',
            td: ''
        }
    }

    componentDidMount() {
        axios.get('http://localhost:4999/description/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    uri: response.data.uri,
                    td: response.data.td
                })
            })
            .catch(function(error) {
                console.log(error)
            })
    }

    onChangeURI(e) {
        this.setState({
            name: e.target.value
        });
    }

    onChangeTD(e) {
        this.setState({
            body: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();
        const obj = {
            uri: this.state.uri,
            td: this.state.td
        };
        axios.post('http://localhost:4999/description/update/'+this.props.match.params.id, obj)
            .then(res => console.log(res.data));

        this.props.history.push('/');
    }

    render() {
        return (
            <div style={{marginTop: 20}}>
                <h3>Alterar TD:</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>URI: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.uri}
                                onChange={this.onChangeURI}
                                />
                    </div>
                    <div className="form-group">
                        <label>TD: </label>
                        <textarea type="text"
                                className="form-control"
                                value={this.state.body}
                                onChange={this.onChangeTD}
                                />
                    </div>
                    <br/>
                    <div className="form-group">
                        <input type="submit" value="Update Description" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
