import React, {Component} from 'react';
import axios from 'axios';

// WoT imports, needed for several components
import * as Core from '@node-wot/core';
import * as Http from '@node-wot/binding-http';
import * as WebSocket from '@node-wot/binding-websockets';


const Description = props => (
    <tr>
        <td>{props.description.id}</td>
        <td>{props.description.title}</td>
    </tr>
)


export default class ReadDesc extends Component {

    constructor(props) {
        var servient;
        super(props);
        this.state = {
            description: [], 
            counterProperties: []};
        this._isMounted = false;

        servient = new Wot.Core.Servient();
        servient.addClientFactory(new Wot.Http.HttpClientFactory());
        window.onload = () => {
            this.get_td(document.getElementById("td_addr").value);
        }

    }


    get_td(addr) {
        this.servient.start().then((thingFactory) => {
            thingFactory.fetch(addr).then((td) => {
                var thing = thingFactory.consume(td);
                this.showInteractions(thing);
            }).catch((error) => {
                window.alert("Could not fetch TD.\n" + error)
            })
        })
    }

    showInteractions(thing) {
        this.counterProperties = [];
        for ( let property in thing.properties ) {
            if (thing.properties.hasOwnProperty(property)) {
                let dtItem = document.createElement("dt");
                this.counterProperties.push(dtItem);
                let ddItem = document.createElement("dd");
                ddItem.setAttribute('dir', 'auto'); // direction-independence, direction-heuristic
                ddItem.appendChild(document.createTextNode("???"));
                let link = document.createElement("a");
                link.appendChild(document.createTextNode(property));
                dtItem.appendChild(link);
                document.getElementById("properties").appendChild(dtItem);
                document.getElementById("properties").appendChild(ddItem);

                dtItem.onclick = (click) => {
                    thing.properties[property].read()
                    .then(
                        res => { ddItem.textContent = res; }
                        // res => window.alert(property + ": " + res)
                    )
                    .catch(err => window.alert("error: " + err))
                }

                // update value right-away
                dtItem.click();
            }
        };
        for ( let action in thing.actions ) {
            if (thing.actions.hasOwnProperty(action)) {
                let item = document.createElement("li");
                item.setAttribute('dir', 'auto'); // direction-independence, direction-heuristic
                let button = document.createElement("button");
                button.appendChild(document.createTextNode(action));
                button.className = "button tiny secondary"
                item.appendChild(button)
                document.getElementById("actions").appendChild(item);

                item.onclick = (click) => { 
                    thing.actions[action].invoke()
                        .then((res) => { 
                            button.style.background = 'rgb(0,255,0,0.2)';
                            setTimeout(function () {
                              button.style.background = null;
                            }, 500);
                            this.updateProperties();
                        })
                        .catch((err) => {
                            button.style.background = 'rgb(255,0,0,0.2)';
                            setTimeout(function () {
                              button.style.background = null;
                            }, 500);
                        })
                }
            }
        };
        let eventSubscriptions = {}
        for ( let evnt in thing.events ) {
            if (thing.events.hasOwnProperty(evnt)) {
                let item = document.createElement("li");
                item.setAttribute('dir', 'auto'); // direction-independence, direction-heuristic
                let link = document.createElement("a");
                link.appendChild(document.createTextNode(evnt));

                let checkbox = document.createElement("div");
                checkbox.className = "switch small"
                checkbox.innerHTML = '<input id="' + evnt + '" type="checkbox">\n<label for="' + evnt + '"></label>'
                item.appendChild(link);
                item.appendChild(checkbox)
                document.getElementById("events").appendChild(item);

                checkbox.onclick = (click) => {
                    if (document.getElementById(evnt).checked && !eventSubscriptions[evnt]) {
                        eventSubscriptions[evnt] = thing.events[evnt].subscribe(
                            (response) => {
                                // window.alert("Event " + evnt + " detected\nMessage: " + response);
                                this.updateProperties();
                            },
                            (error) => { window.alert("Event " + evnt + " error\nMessage: " + error); }
                        )
                    } else if (!document.getElementById(evnt).checked && eventSubscriptions[evnt]) {
                        eventSubscriptions[evnt].unsubscribe();
                    }
                }
            }
        };
        // Check if visible
        let placeholder = document.getElementById("interactions")
        if ( placeholder.style.display === "none") {
            placeholder.style.display = "block"
        }
    }

    updateProperties() {
        this.counterProperties.forEach(function(prop) {
          prop.click();
        });
    }

    componentWillUnmount() {
        this._isMounted = false;

    }

    componentDidMount() {
        this._isMounted = true;

        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    description: response.data
                })
            })
            .catch(function(error) {
                console.log(error)
            })
    }

    componentDidUpdate() {
        axios.get('http://localhost:4000/td/')
        .then(response => {
            this.setState({description: response.data});
        })
        .catch(function (error) {
            console.log(error);
        })   
    }

    descriptionList() {
        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
        .then(response => {
            this.state.description.push({
                    id:response.data.id,
                    title: response.data.title
                });
            })
        .catch(function(error) {
            console.log(error)
        })
        return <Description description={this.state.description} />;
    }

    render() {
        return (
            <div style={{ marginTop: 20 }}>
                <h3>Catálogo</h3>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.descriptionList() }    
                    </tbody>
                </table>
        <div id="topbar" class="row">
            <div class="medium-12 columns">
              <h1>Counter Client Example in the Browser</h1>
              <input type="hidden" id="td_addr" type="url" value="http://plugfest.thingweb.io:8083/counter"/>
            </div>
        </div>
          <div id="interactions" class="row" >
            <div class="medium-4 columns">
              <figure>
                <figcaption><h4>Properties</h4></figcaption>
                <dl id="properties" class="side-nav"></dl>
              </figure>
            </div>
            <div class="medium-4 columns">
              <figure>
                <figcaption><h4>Actions</h4></figcaption>
                <ul id="actions" class="side-nav"></ul>
              </figure>
            </div>
            <div class="medium-4 columns">
              <figure>
                <figcaption><h4>Events</h4></figcaption>
                <ul id="events" class="side-nav"></ul>
              </figure>
            </div>
          </div>
          </div>
        )
    }


}
