import React, {Component} from 'react';
import axios from 'axios';

class ReadDesc extends Component {

    constructor(props) {
        super(props);
        this.state = {
        things: []
        };
    }

    addThing = thing => {
        const { things }  = this.state;
        this.setState({ things : [...things, thing] });
    };

    loadThing = () => {
        const { WoT } = this.props;
        const res =  fetch(axios.get('http://localhost:4000/td/'+this.props.match.params.id));
        const td = res.text();
        const thing = WoT.consume(td);
        this.addThing(thing);
    }

    render = () => {
        const { things } = this.state;
        return (
            <WoTProvider>
                <ul>
                {
                    things.map(thing => 
                    <li key={thing.id}>{thing.name}</li>)
                }
                </ul>
                <Button onClick={this.loadThing()}>
                    Load a thing
                </Button>
            </WoTProvider>
        );
    };
}
export default withWoT(ReadDesc);
