import React, {Component} from 'react';
import axios from 'axios';

const Description = props => (
    <tr>
        <td>{props.description.context}</td>
        <td>{props.description.id}</td>
        <td>{props.description.title}</td>
        <td>{props.description.properties}</td>
        <td>{props.description.actions}</td>
        <td>{props.description.events}</td>
    </tr>
)

export default class ReadDesc extends Component {

    constructor(props) {
        super(props);
        this.state = {description: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
            .then(response => {
                this.setState({description: response.data});
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    descriptionList() {
        return <Description description={this.state.description} key={this.state.description.id} />;
    }

    render() {
        return (
            <div style={{ marginTop: 20 }}>
                <h3>Cat√°logo</h3>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>Context</th>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Properties</th>
                            <th>Actions</th>
                            <th>Events</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.descriptionList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
