import React, {Component} from 'react';
import axios from 'axios';


export default class SearchDesc extends Component {

    constructor(props) {
        super(props);

        this.onChangeBusca = this.onChangeBusca.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            texto: ''
        }

        this._isMounted = false;
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    componentDidMount() {
        this._isMounted = true;
    }

    onChangeBusca(e) {
        this.setState({
            texto: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Text send: ${this.state.texto}`);

        const newData = {
            texto: this.state.texto
        }

        axios.get('http://localhost:4000/td/search', newData)
            .then(res => console.log(res.data));

        axios.
        this.setState({
            texto: ''
        })
    }

    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <section class="formulario mb-5">
                    <div style={{ marginTop: 20 }}>
                        <div className="form-group">

                            <div class="formul">
                                <label for="busca">Localizar TD's dispon√≠veis</label>
                                <textarea class="texto-form" id="busca" rows="5" cols="100" onChange={this.onChangeBusca}></textarea>
                            </div>


                            <div class="row d-flex align-items-center mb-3 mt-4">
                                <div class="col-md-12">
                                    <div class="text-center">
                                        <button type="button" class="btn btn-outline-dark">Buscar</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </form>
        )
    }
}
