    constructor(props) {
        let counterProperties = [];
        var servient;
        super(props);
        this.state = {description: []};
        this._isMounted = false;
    }




    updateProperties() {
        counterProperties.forEach(function(prop) {
          prop.click();
        });
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    componentDidMount() {
        this._isMounted = true;

        servient = new Wot.Core.Servient();
        servient.addClientFactory(new Wot.Http.HttpClientFactory());
        window.onload = () => {
            get_td(document.getElementById("td_addr").value);
        }

        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    description: response.data
                })
            })
            .catch(function(error) {
                console.log(error)
            })
    }

    componentDidUpdate() {
        axios.get('http://localhost:4000/td/')
        .then(response => {
            this.setState({description: response.data});
        })
        .catch(function (error) {
            console.log(error);
        })   
    }

    descriptionList() {
        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
        .then(response => {
            this.state.description.push({
                    id:response.data.id,
                    title: response.data.title
                });
            })
        .catch(function(error) {
            console.log(error)
        })
        return <Description description={this.state.description} />;
    }

    render() {
        return (
            <div style={{ marginTop: 20 }}>
                <h3>Cat√°logo</h3>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.descriptionList() }    
                    </tbody>
                </table>
        <div id="topbar" class="row">
            <div class="medium-12 columns">
              <h1>Counter Client Example in the Browser</h1>
              <input type="hidden" id="td_addr" type="url" value="http://plugfest.thingweb.io:8083/counter"/>
            </div>
        </div>
          <div id="interactions" class="row" style="display: none;">
            <div class="medium-4 columns">
              <figure>
                <figcaption><h4>Properties</h4></figcaption>
                <dl id="properties" class="side-nav"></dl>
              </figure>
            </div>
            <div class="medium-4 columns">
              <figure>
                <figcaption><h4>Actions</h4></figcaption>
                <ul id="actions" class="side-nav"></ul>
              </figure>
            </div>
            <div class="medium-4 columns">
              <figure>
                <figcaption><h4>Events</h4></figcaption>
                <ul id="events" class="side-nav"></ul>
              </figure>
            </div>
          </div>
          </div>
        )
    }
}
