import React, {Component} from 'react';
import axios from 'axios';

const Description = props => (
    <tr>
        <td>{props.description.id}</td>
        <td>{props.description.title}</td>
    </tr>
)

export default class ReadDesc extends Component {

    constructor(props) {
        super(props);
        this.state = {description: []};
        this._isMounted = false;
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    componentDidMount() {
        this._isMounted = true;

        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    description: response.data
                })
            })
            .catch(function(error) {
                console.log(error)
            })
    }

    componentDidUpdate() {
        axios.get('http://localhost:4000/td/')
        .then(response => {
            this.setState({description: response.data});
        })
        .catch(function (error) {
            console.log(error);
        })   
    }

    descriptionList() {
        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
        .then(response => {
            this.state.description.push({
                    id:response.data.id,
                    title: response.data.title
                });
            })
        .catch(function(error) {
            console.log(error)
        })
        return <Description description={this.state.description} />;
    }

    render() {
        return (
            <div style={{ marginTop: 20 }}>
                <h3>Cat√°logo</h3>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.descriptionList() }    
                    </tbody>
                </table>
            </div>
        )
    }
}
