import React, {Component} from 'react';
import axios from 'axios';


export default class SearchDesc extends Component {

    constructor(props) {
        super(props);

        this.onChangeBusca = this.onChangeBusca.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            texto: ''
        }

        this._isMounted = false;
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    componentDidMount() {
        this._isMounted = true;
        axios.get('http://localhost:4000/td/')
            .then(response => {
                this.setState({description: response.data});
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    onChangeBusca(e) {
        this.setState({
            texto: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Text send: ${this.state.texto}`);

        const newData =  this.state.texto

        axios.get('http://localhost:4000/td/search', newData)
            .then(res => console.log(res.data));

        this.setState({
            texto: ''
        })
    }

    render() {
        return (
            <div style={{marginTop: 20}}>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Localizar TD's dispon√≠veis </label>
                            <div class="formul">
                                <textarea class="texto-form" id="busca" rows="5" cols="100" onChange={this.onChangeBusca}></textarea>
                            </div>
                    </div>
                    <div className="form-group">
                        <div class="text-center">
                            <input type="submit" value="Buscar" className="btn btn-outline-dark" />    
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}
