import React, {Component} from 'react';
import axios from 'axios';

export default class CreateDesc extends Component {

    constructor(props) {
        super(props);

        this.onChangeURL = this.onChangeURL.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            url: '',
            description: {}
        }
    }

    onChangeURL(e) {
        this.setState({
            url: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        var url = this.state.url

        console.log(`Form submitted: ` + url);

        axios.get(url)
            .then(response => {
                this.setState({description: response.body});
                console.log('Descricao encontrada: ' + this.state.description)
            })
            .catch(function (error) {
                console.log('Falha: ' + error);
            })


        axios.post('http://localhost:4000/td/add', this.state.description)
            .then(res => console.log(res.data));

    }

    render() {
        return (
            <div style={{marginTop: 20}}>
                <h3>Adicionar TD</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>URL</label>
                        <input type="text"
                                className="form-control"
                                value={this.state.url}
                                onChange={this.onChangeURL}
                                />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Adicionar" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
