import React, {Component} from 'react';
import axios from 'axios';


// Importandando bibliotecas necessarias do WoT
import * as Core from '@node-wot/core';
import * as Http from '@node-wot/binding-http';
import * as WebSocket from '@node-wot/binding-websockets';

export const WoT = {
  Core,
  Http,
  WebSocket
};

const Description = props => (
    <tr>
        <td>{props.description.id}</td>
        <td>{props.description.title}</td>
    </tr>
)

export default class ReadDesc extends Component {

    constructor(props) {
        super(props);
        this.state = {description: [], wot: {}};
        this._isMounted = false;
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    componentDidMount() {

        const servient = new WoT.Core.Servient();
        servient.addClientFactory(new WoT.Http.HttpClientFactory());
        servient.addClientFactory(new WoT.WebSocket.WebSocketClientFactory());
        servient.addClientFactory(new WoT.WebSocket.WebSocketSecureClientFactory());
        servient.start().then(wot => {
            this.setState({ wot });
        });

        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
        .then(response => {
            this.setState({
                description: response.data
            })
        })
        .catch(function(error) {
            console.log(error)
        })

        this._isMounted = true;
    }

    componentDidUpdate() {
        axios.get('http://localhost:4000/td/')
        .then(response => {
            this.setState({description: response.data});
        })
        .catch(function (error) {
            console.log(error);
        })   
    }

    descriptionList() {
        axios.get('http://localhost:4000/td/'+this.props.match.params.id)
        .then(response => {
            this.state.description.push({
                    name:response.data.name,
                    title: response.data.title,
                    desc: response.data.desc,
                    body: response.data.body
                });
            })
        .catch(function(error) {
            console.log(error)
        })
        return <Description description={this.state.description} />;
    }

    render() {
        return (
            <div style={{ marginTop: 20 }}>
                <h3>Cat√°logo</h3>

                <WoTContext.Provider value={{ wot: this.state.wot }}>
                    {this.props.children}
                </WoTContext.Provider>
            </div>
        )
    }
}

export const WoTContext = React.createContext({ wot: {} });

export const withWoT = ComponentToWrap => {
  class WithWoT extends React.Component {
    render() {
      let WoT = this.context.wot;
      return <ComponentToWrap {...this.props} WoT={WoT} />;
    }
  }
  WithWoT.contextType = WoTContext;
  return WithWoT;
};
